import { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField } from "@mui/material";

export default function SorteioFutebol() {
  const [history, setHistory] = useState([]);
  const [currentDraw, setCurrentDraw] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [userName, setUserName] = useState("");
  const [players, setPlayers] = useState(Array(10).fill(""));
  const [attemptCount, setAttemptCount] = useState(0);

  useEffect(() => {
    const storedHistory = JSON.parse(localStorage.getItem("sorteios")) || [];
    setHistory(storedHistory);
  }, []);

  const realizarSorteio = () => {
    setOpenDialog(true);
  };

  const confirmarSorteio = () => {
    if (!userName || players.some(player => player.trim() === "")) return;

    const shuffledPlayers = [...players].sort(() => Math.random() - 0.5);
    const team1 = shuffledPlayers.slice(0, 5);
    const team2 = shuffledPlayers.slice(5, 10);
    const teamColors = Math.random() < 0.5 ? ["Branca", "Colorida"] : ["Colorida", "Branca"];

    const newAttemptCount = attemptCount + 1;
    setAttemptCount(newAttemptCount);

    const result = {
      id: uuidv4(),
      date: new Date().toLocaleString(),
      name: userName,
      attempt: newAttemptCount,
      teams: [
        { name: teamColors[0], players: team1 },
        { name: teamColors[1], players: team2 },
      ],
    };

    const updatedHistory = [result, ...history];
    setHistory(updatedHistory);
    setCurrentDraw(result);
    localStorage.setItem("sorteios", JSON.stringify(updatedHistory));
    setOpenDialog(false);
    setUserName("");
    setPlayers(Array(10).fill(""));
  };

  return (
    <div className="flex flex-col md:flex-row h-screen bg-gray-100 p-6">
      {/* Barra lateral com histórico */}
      <div className="w-full md:w-1/3 bg-white p-4 shadow-md rounded-lg overflow-auto">
        <h2 className="text-xl font-bold mb-4 text-center text-gray-700">📜 Sorteios Anteriores</h2>
        <ul className="space-y-2">
          {history.map((draw) => (
            <li
              key={draw.id}
              className="cursor-pointer p-3 border rounded-lg hover:bg-gray-200 transition"
              onClick={() => setCurrentDraw(draw)}
            >
              <strong>{draw.name}</strong> - {draw.date} <span className="text-gray-500">(Tentativa: {draw.attempt})</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Conteúdo principal */}
      <div className="flex-1 p-6 flex flex-col items-center justify-center">
        <h1 className="text-3xl font-bold mb-4 text-gray-800">⚽ Peladinha - Sorteio de equipas</h1>
        <button
          onClick={realizarSorteio}
          className="px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-700 transition mb-4"
        >
          🎲 Realizar Sorteio
        </button>

        {/* Resultado do sorteio */}
        {currentDraw && (
          <div className="mt-4 p-6 bg-white border rounded-lg shadow-lg w-full max-w-lg">
            <h2 className="text-2xl font-semibold text-center text-gray-700">🎯 Resultado do Sorteio</h2>
            <p className="text-gray-600 text-center">Sorteado por: <strong>{currentDraw.name}</strong></p>
            <p className="text-gray-600 text-center">📅 {currentDraw.date}</p>
            <p className="text-gray-600 text-center">🔄 Tentativa Nº: {currentDraw.attempt}</p>

            {currentDraw.teams.map((team, index) => (
              <div key={index} className={`mt-4 p-4 rounded-lg ${team.name === "Branca" ? "bg-blue-100" : "bg-red-100"}`}>
                <h3 className={`text-lg font-bold text-center ${team.name === "Branca" ? "text-blue-600" : "text-red-600"}`}>
                  {team.name} 🏆
                </h3>
                <ul className="list-disc ml-6 text-gray-800">
                  {team.players.map((player, idx) => (
                    <li key={idx}>{player}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Modal de entrada */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>🎲 Informações do Sorteio</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nome do Sorteador"
            fullWidth
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          {players.map((player, index) => (
            <TextField
              key={index}
              margin="dense"
              label={`Jogador ${index + 1}`}
              fullWidth
              value={player}
              onChange={(e) => {
                const newPlayers = [...players];
                newPlayers[index] = e.target.value;
                setPlayers(newPlayers);
              }}
            />
          ))}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancelar</Button>
          <Button onClick={confirmarSorteio} color="primary">Confirmar</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
