import { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, 
  Button, TextField, Paper, Typography, Box, List, ListItem, 
  ListItemText, Container, Card, CardContent
} from "@mui/material";

export default function SorteioFutebol() {
  const [history, setHistory] = useState([]);
  const [currentDraw, setCurrentDraw] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [userName, setUserName] = useState("");
  const [players, setPlayers] = useState(Array(10).fill(""));
  const [attemptCount, setAttemptCount] = useState(0);

  useEffect(() => {
    const storedHistory = JSON.parse(localStorage.getItem("sorteios")) || [];
    setHistory(storedHistory);
  }, []);

  const realizarSorteio = () => {
    setOpenDialog(true);
  };

  const confirmarSorteio = () => {
    if (!userName || players.some(player => player.trim() === "")) return;

    const shuffledPlayers = [...players].sort(() => Math.random() - 0.5);
    const team1 = shuffledPlayers.slice(0, 5);
    const team2 = shuffledPlayers.slice(5, 10);
    const teamColors = Math.random() < 0.5 ? ["Branca", "Colorida"] : ["Colorida", "Branca"];

    const newAttemptCount = attemptCount + 1;
    setAttemptCount(newAttemptCount);

    const result = {
      id: uuidv4(),
      date: new Date().toLocaleString(),
      name: userName,
      attempt: newAttemptCount,
      teams: [
        { name: teamColors[0], players: team1 },
        { name: teamColors[1], players: team2 },
      ],
    };

    const updatedHistory = [result, ...history];
    setHistory(updatedHistory);
    setCurrentDraw(result);
    localStorage.setItem("sorteios", JSON.stringify(updatedHistory));
    setOpenDialog(false);
    setUserName("");
    setPlayers(Array(10).fill(""));
  };

  return (
    <Container>
      {/* Hist√≥rico de sorteios */}
      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h5" align="center" gutterBottom>
          üìú Sorteios Anteriores
        </Typography>
        <List>
          {history.map((draw) => (
            <ListItem 
              button 
              key={draw.id} 
              onClick={() => setCurrentDraw(draw)} 
              sx={{ borderBottom: "1px solid #ddd" }}
            >
              <ListItemText 
                primary={<strong>{draw.name}</strong>} 
                secondary={`${draw.date} (Tentativa: ${draw.attempt})`} 
              />
            </ListItem>
          ))}
        </List>
      </Paper>

      {/* Bot√£o de sorteio */}
      <Box textAlign="center" mb={3}>
        <Typography variant="h4" gutterBottom>
          ‚öΩ Peladinha - Sorteio de equipas
        </Typography>
        <Button variant="contained" color="primary" onClick={realizarSorteio}>
          üé≤ Sortear equipas
        </Button>
      </Box>

      {/* Resultado do sorteio */}
      {currentDraw && (
        <Card elevation={3} sx={{ maxWidth: 500, mx: "auto", p: 3 }}>
          <Typography variant="h5" align="center">
            üéØ Resultado do Sorteio
          </Typography>
          <Typography align="center">Sorteado por: <strong>{currentDraw.name}</strong></Typography>
          <Typography align="center">üìÖ {currentDraw.date}</Typography>
          <Typography align="center">üîÑ Tentativa N¬∫: {currentDraw.attempt}</Typography>

          {currentDraw.teams.map((team, index) => (
            <CardContent 
              key={index} 
              sx={{
                mt: 2, 
                backgroundColor: team.name === "Branca" ? "#e3f2fd" : "#ffebee", 
                borderRadius: 2
              }}
            >
              <Typography variant="h6" align="center" sx={{ fontWeight: "bold", color: team.name === "Branca" ? "#1565c0" : "#b71c1c" }}>
                {team.name} üèÜ
              </Typography>
              <List>
                {team.players.map((player, idx) => (
                  <ListItem key={idx}>
                    <ListItemText primary={player} />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          ))}
        </Card>
      )}

      {/* Modal de entrada */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>üé≤ Informa√ß√µes do Sorteio</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nome do Sorteador"
            fullWidth
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          {players.map((player, index) => (
            <TextField
              key={index}
              margin="dense"
              label={`Jogador ${index + 1}`}
              fullWidth
              value={player}
              onChange={(e) => {
                const newPlayers = [...players];
                newPlayers[index] = e.target.value;
                setPlayers(newPlayers);
              }}
            />
          ))}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancelar</Button>
          <Button onClick={confirmarSorteio} color="primary">Confirmar</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}
